                      <OrmFieldDisplay v-if="sourceTypeDef.fields[fieldName] || fieldName.startsWith('_meta')" :field="fieldName.startsWith('_meta') ? metaField(fieldName) : sourceTypeDef.fields[fieldName]" :value="deepGet(obj, fieldName)" />
                    </td>
